// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.17;

// interfaces
import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "../interfaces/IStaking.sol";

contract CheckBlockTest {
    IStaking staking;
    IERC20 sdex;

    constructor(IStaking _staking, IERC20 _sdex) {
        staking = _staking;
        sdex = _sdex;
        sdex.approve(address(staking), type(uint256).max);
    }

    function exploitStaking(uint256 amount) external {
        staking.deposit(amount);
        staking.withdraw(address(this), amount * 1e27);
    }
}

contracts/rewards/test/MultiCall.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

contract MultiCall {
    function multiCall(address _target, bytes[] calldata _data) external returns (bytes[] memory) {
        uint256 _dataLength = _data.length;
        bytes[] memory _results = new bytes[](_dataLength);

        for (uint _i; _i < _dataLength; ) {
            (bool _success, bytes memory _result) = _target.call(_data[_i]);
            require(_success, string(_result));
            _results[_i] = _result;

            unchecked {
                ++_i;
            }
        }

        return _results;
    }
}

